CREATE TABLE "AlertHost" ( `AlertHostId` INTEGER NOT NULL, `HostId` INTEGER NOT NULL, `UUID` TEXT NOT NULL, PRIMARY KEY(`AlertHostId`) );
CREATE TABLE "AlertIPPort" ( `IPPortAlertId` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, `IPPortId` INTEGER NOT NULL, `UUID` TEXT NOT NULL );
CREATE TABLE "Host" ( `HostId` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, `Domain` TEXT NOT NULL, `Host` TEXT NOT NULL, `Status` INTEGER NOT NULL DEFAULT 0, `Alert` INTEGER NOT NULL DEFAULT 0, `TimeScanned` NUMERIC NOT NULL );
CREATE TABLE "IPPort" ( `IPPortId` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, `IP` INTEGER NOT NULL, `Protocol` TEXT NOT NULL, `Port` TEXT NOT NULL, `Service` TEXT, `IsApproved` INTEGER NOT NULL DEFAULT 0, `Alert` INTEGER DEFAULT 0, `TimeScanned` NUMERIC NOT NULL );
CREATE TABLE "IPHost" ( `IPHostId` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, `Domain` TEXT NOT NULL, `IP` TEXT NOT NULL, `Host` TEXT NOT NULL, `Status` INTEGER NOT NULL DEFAULT 0, `Alert` INTEGER NOT NULL DEFAULT 0, `TimeScanned` INTEGER NOT NULL );
